import React, { Component } from 'react'
import Particle from './Particle'

export default class Contact extends Component {

    state={
        firstName: '',
        lastName: '',
        email: '',
        message: '',
        sent: false
    }

    handlefirstName=(e)=>{
        this.setState({
            firstName: e.target.firstName
        })
    }
    handlelastName=(e)=>{
        this.setState({
            firstName: e.target.firstName
        })
    }
    handlefirstName=(e)=>{
        this.setState({
            firstName: e.target.firstName
        })
    }
    handlefirstName=(e)=>{
        this.setState({
            firstName: e.target.firstName
        })
    }



    render() {
        return (
            <>
                <Particle height="25rem" />
                <div className="common-container">
                    <div className="inner-container">
                        <h1>Contact Me</h1>
                        <div className="contact-contents">
                            <div>
                                <label htmlFor="firstName">First Name :</label>
                                <input type="text" name="firstName" className="firstName" placeholder="Enter your First Name...." />
                            </div>
                            <div>
                                <label htmlFor="lastName">Last Name :</label>
                                <input type="text" name="lastName" className="lastName" placeholder="Enter your Last Name..." />
                            </div>
                            <div>
                                <label htmlFor="email">Email :</label>
                                <input type="text" name="email" className="email" placeholder="Enter your Email..." />
                            </div>
                            <div>
                                <label htmlFor="message">Message :</label>
                                <textarea name="message" className="message" placeholder="Enter your Message..." />
                            </div>
                            <div className="msg-sent">Message has been sent!</div>
                            <div>
                                <button className="button-contact" type="submit">Submit</button>
                            </div>
                        </div>
                    </div>
                </div>
            </>

        )
    }
}
