import React, { useState } from 'react';
// import Popup from "./Popup";
import { Modal, Button } from "react-bootstrap";

export default function Card(props) {

    const [openPop, setopenPop] = useState(false);


    return (
        <>
            <div className="project-card">
                <div className="popup-behind">
                    <h2>{props.text}</h2>
                    <h3 className="card-inner-text">A simple website to showcase my photography skills.</h3>
                    <Button className="button" onClick={() => setopenPop(true)}>
                        Explore it
                    </Button>
                    <NavLink className="button" onClick={() => setopenPop(true)}>More about me</NavLink>
                </div>

                <div className="popup-front">
                    <Modal size="lg" show={openPop} className="modal-inner" onHide={() => setopenPop(false)}>
                        <div className="modal-outer">
                            <Modal.Body>
                                <h3>{props.popupText}</h3>
                            </Modal.Body>
                            <Button  onClick={() => setopenPop(false)}>Close Popup</Button>
                        </div>
                    </Modal>
                </div>
            </div>
        </>
    )
}
